import React from 'react'
import { useNavigate } from 'react-router-dom';

const AdminLogin = () => {
    let navigate = useNavigate();
    const Cancelbtn = () => {
        navigate('/')
    }

    const [err, setErr] = useState({
        status: false,
        msg: '',
        type: ''
    })
    const nextAdminPage = (e) => {
        e.preventDefault();
        const data = new FormData(e.currentTarget);
        const actualData = {
            email: data.get('email'),
            password: data.get('password'),
            // tc: data.get('tc'),
        }
        if (actualData.email && actualData.password && actualData.tc) {
            console.log(actualData);
            document.getElementById('login-form').reset()
            setErr({ status: true, msg: 'Login Successfully. Redirect to Dashboard...', type:'success' })
            setTimeout(()=>{
                navigate("/VendorPage")
            },2000)
        } else {
            setErr({ status: true, msg: 'Enter your email and password', type:'error' })
        }
    }

    return (
        <div className=" min-h-screen flex flex-col items-center justify-center">
            <h1 className='font-bold text-3xl text-white bg-indigo-500 border-0 py-2 px-6 focus:outline-none hover:bg-indigo-600 rounded'>Event Management System</h1>
            <div className="bg-white p-8 rounded shadow-md w-[30rem] space-y-10 ">
                <div class="relative flex gap-10 items-center justify-center">
                    <div>User Id</div>
                    <div>
                        <input type="text" id="email" name="name" placeholder='Admin Id' class="w-full bg-white rounded border border-gray-300 focus:border-indigo-500 focus:ring-2 focus:ring-indigo-200 text-base outline-none text-gray-700 py-1 px-3 leading-8 transition-colors duration-200 ease-in-out" />
                    </div>
                </div>
                <div class="relative flex gap-10 items-center justify-center">
                    <div>Password</div>
                    <div>
                        <input type="text" id="name" name="name" placeholder='Admin Password' class="w-full bg-white rounded border border-gray-300 focus:border-indigo-500 focus:ring-2 focus:ring-indigo-200 text-base outline-none text-gray-700 py-1 px-3 leading-8 transition-colors duration-200 ease-in-out" />
                    </div>
                </div>
                <div className='flex gap-10 px-36'>
                    <button onClick={Cancelbtn} className="text-white bg-indigo-500 border-0 py-2 px-6 focus:outline-none hover:bg-indigo-600 rounded text-lg">Cancel</button>
                    <button onClick={nextAdminPage} className="text-white bg-indigo-500 border-0 py-2 px-6 focus:outline-none hover:bg-indigo-600 rounded text-lg">Submit</button>
                </div>
            </div>
        </div>
    )
}

export default AdminLogin
